public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		if (num>0) {
			System.out.println("Positive Number:"+num);
		}else {
			System.out.println("Negative Number:"+num);
			
			
		}

	}

}
